import React, { Component } from 'react';
import Axios from 'axios';

export default class Report extends Component {
    constructor(props) {
        super(props);
        this.onChange = this.onChange.bind(this);
        this.bindddlData =this.bindddlData.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            PredictedData: {
                predictedValue: ''
            },
            data:[],
        }
    }

    componentDidMount(){
        this.bindddlData();
    }
    onChange(e) {
        const name = e.target.name;
        const value = e.target.value;
        this.setState({
            ...this.PredictedData,
            [name]: value,
        })
    }
    bindddlData() {
        Axios({
            method: "get",
            url: 'http://localhost:8088/getData',
            responseType: 'json'
        }).then((responce) => {
            console.log(responce);
            this.setState({
                data:responce.data
            })
        })
    }

    onSubmit(){
        insertPredictedValue(this.state.PredictedData)
    }

    insertPredictedValue(data){
        Axios({
            method:"post",
            url:'http://localhost:8088/insertPredictedValue',
            data:data,
            responceType:'json'
        }).then((x)=>{
            console.log(x);
            
        })
    }

    render() {
        let ddlValue = this.state.data.map((element)=>{
            return(
                <option>{element.name}</option>
            )
        })
        return (
            <React.Fragment>
                <h1>This is Report Page</h1>
                <select>{ddlValue}</select>
                <input type="text" placeholder='Enter Predicted Value' name='predictedValue' onChange={this.onChange} autoComplete='off' />
                <input type="button" name="submitPredictedValue" value='submit' onClick=''/>
            </React.Fragment>
        )
    }
}
